% Autores: GRUPO 6: Cristian Marin, Jorge Sanchez, Antony Uquillas%% Fecha: Enero/2022% Esta función resuelve la ecuacion del calor a través del método de diferencias finitas. function [x,U]=ecuacioncalor(f, a1, a2, a, b, c, n, m, fi, fo)    % Condiciones del metodo del metodo --------------------    h=a/(n-1); %Valor de la variacion  k=b/(m-1); %Valor de la variacion   r=c^2*(k/h^2);  % Determinar k^2*?u  s=1-2*r;   U=zeros(n,m);  U(1,1:m)=a1;  U(n,1:m)=a2;  U(2:n-1,1)=(f(h:h:(n-2)*h))';    % Iteracion del metodo ---------------------  % Expansion de la serie de Taylor ----------  % Para la segunda dervida  for j=2:m    for i=2:n-1      U(i,j)=s*U(i,j-1)+r*(U(i-1,j-1)+U(i+1,j-1));    endfor  endfor    % Graficacion ------------------------------  figure(fi)  U=U'; %Matriz transpuesta conjugada compleja  surf(U)  x=0:h:a;  title('Representación 3D de dominio')  figure(fo)  for i=1:n-1    plot(x,U(i,:))    title("Representacion 2D")    hold on   endforendfunction  